function setCookie(e,o,t){var i=new Date;if(i.setTime(i.getTime()+24*t*60*60*1e3),null!=t&&t>0){var c="expires="+i.toUTCString();document.cookie=e+"="+o+";"+c}else document.cookie=e+"="+o+";path=/"}function getCookie(e){for(var o=e+"=",t=document.cookie.split(";"),i=0;i<t.length;i++){for(var c=t[i];" "==c.charAt(0);)c=c.substring(1);if(-1!=c.indexOf(o))return c.substring(o.length,c.length)}return""}let cookieId=document.querySelector("#cookie"),cookieClose=document.querySelector(".cookie-close");cookieClose.addEventListener("click",(function(){cookieId.remove()}));let cookie_value=getCookie("web_view");""!=cookie_value&&cookieId.remove();